# OEIS sequence: A347401

def ispal(n): s = str(n); return s == s[::-1]
def aupton(terms):
    alst, seen = [1], {1}
    for n in range(2, terms+1):
        an = 1
        while an in seen or not ispal(n + an): an += 1
        alst.append(an); seen.add(an)
    return alst
print(aupton(200)) # _Michael S. Branicky_, Aug 30 2021

