# OEIS sequence: A347409

def A347409(n):
    m, r = n, 0
    while m > 1:
        if m % 2:
            m = 3*m + 1
        else:
            s = bin(m)[2:]
            c = len(s)-len(s.rstrip('0'))
            m //= 2**c
            r = max(r,c)
    return r # _Chai Wah Wu_, Sep 29 2021

