from functools import lru_cache
@lru_cache(maxsize=None)
def b(n, i):
    if n > i*(i+1)*(i+2)//6: return 0
    if i == 0: return 1
    return b(n+i*(i+1)//2, i-1) + b(abs(n-i*(i+1)//2), i-1)
def a(n): return b(0, n) + b(1, n)
