def is_ok(m):
    f = factorint(m)
    return (
        sum(f.values()) == len(f) + 1 and
        all((q - 1) % p > 0 for p in f for q in f) and
        (m := next(p for p, e in f.items() if e == 2) ** 2 - 1) and
        all(m % q > 0 for q in f)) # _David Radcliffe_, Jul 30 2025

