from sympy import factorint
def is_ok(m):
    f = factorint(m)
    if any(e > 1 for e in f.values()): return False # m must be squarefree
    return sum(q % p == 1 for p in f for q in f) == 1 # _David Radcliffe_, Jul 30 2025

