from sympy import factorint
def A037276(n):
    if n == 1: return 1
    return int("".join(str(p)*e for p, e in sorted(factorint(n).items())))
def afind(limit, startk=1):
    for k in range(startk, limit+1):
        if (A037276(k) + 1)%k == 0:
            print(k, end=", ")
