from sympy import isprime, nextprime
from itertools import islice
def valuation(n, p):
    v = 0
    while n%p == 0: n //= p; v += 1
    return v, n
def agen(): # generator of terms
    p = 2
    while True:
        q, r = valuation(p+1, 2)
        if isprime(q) and isprime(r): yield p
        p = nextprime(p)
print(list(islice(agen(), 49))) # _Michael S. Branicky_, May 01 2022

