# OEIS sequence: A353152

from sympy import mobius, integer_nthroot
def A353152(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        kmin = kmax >> 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x): return int(n+x+sum(mobius(k)*(integer_nthroot(x,k)[0]//5) for k in range(2,x.bit_length())))
    return bisection(f,n,n) # _Chai Wah Wu_, Sep 18 2025

