from itertools import islice
from sympy import isprime, nextprime
def agen(): # generator of terms
    p, q = 2, 3
    while  True:
        if isprime((q-p)*p*q+1):
            yield p
        p, q = q, nextprime(q)
print(list(islice(agen(), 20))) # _Michael S. Branicky_, Apr 27 2022

