from sympy import isprime, nextprime
def a(n):
    p = 2
    while True:
        k, p = 5**n + p, nextprime(p)
        if all(isprime(k-5**i) for i in range(1, n)):
            return k
print([a(n) for n in range(1, 10)]) # _Michael S. Branicky_, May 30 2022

