from math import gcd
from sympy import sieve, isprime
def A354966(n):
    a, s = gcd(n,24), sum(sieve[j]**2 for j in range(1,n+1))
    for i in count(1):
        (b, c), p = divmod(s,a), sieve[i]
        if c == 0 and isprime(b):
            return p
        s += sieve[i+n]**2-p**2 # _Chai Wah Wu_, Jun 14 2022

