from sympy import divisors, isprime
def ok(n):
    if not isprime(n): return False
    t = n**2 - 1
    return not any(isprime(d+t//d) for d in divisors(t, generator=True))
print([k for k in range(19000) if ok(k)]) # _Michael S. Branicky_, Jul 11 2022

