# OEIS sequence: A355717

def A355717_list(ln: int) -> list[int]:
    P: list[int] = []
    Q: list[int] = [0]
    def is_gpn(k: int) -> bool:
        return any(8 * k == ((n + n % 2) * (3 * n + 2 - n % 2)) for n in range(k + 1))
    for k in range(1, ln):
        q = 3
        if is_gpn(k):
            P.append(k)
            q = 1
        elif any([(k - p) in P for p in P]):
            q = 2
        Q.append(q)
    return Q
