from sympy import isprime
from itertools import count, islice
def fd(d, b): return sum(di*b**i for i, di in enumerate(d[::-1]))
def anext(alst):
    b = max(alst)
    return next(k for k in count(1) if isprime(fd(alst+[k], max(b, k)+1)))
def agen():
    alst = [0]
