from math import gcd
from itertools import count, islice
def agen(): # generator of terms
    an = 1
    for n in count(2):
        yield an
        x = gcd(an, n)
        an = an + n if x == 1 or x == an else an//x
print(list(islice(agen(), 70))) # _Michael S. Branicky_, Dec 15 2022

