from sympy import isprime
def ok(n):
    if not isprime(n): return False
    s = str(n)
    b = int(max(s)) + 1
    return b != 10 and isprime(int(s, b))
print([k for k in range(1600) if ok(k)]) # _Michael S. Branicky_, Jan 27 2023

