# OEIS sequence: A360494

from sympy import isprime
from itertools import count, islice, product
def f(s): return next(b-1 for b in count(2) if not isprime(int(s, b)))
def agen():
    n, adict = 2, {2:11, 3:10}
    for d in count(3):
        for b in product("01", repeat=d-2):
            s = "1" + "".join(b) + "1"
            v = f(s)
            if v not in adict: adict[v] = int(s)
            while n in adict: yield adict[n]; n += 1
print(list(islice(agen(), 7))) # _Michael S. Branicky_, Feb 09 2023

