from sympy import primepi, integer_nthroot
def A361116(n):
    def f(x): return int(n+sum(primepi(integer_nthroot(x,k)[0]) for k in range(1,x.bit_length())))
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
