from sympy import primepi, integer_nthroot, kronecker_symbol
def A367013(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x): return int(n+x-sum(primepi(integer_nthroot(x,k)[0]) for k in range(1,x.bit_length())))
    return (m:=bisection(f,n,n))-kronecker_symbol(-4,m) # _Chai Wah Wu_, Jan 19 2025

