from sympy import nextprime, prevprime
from math import isqrt
def hailstone(prime):
    if (prime + 1) % 6 == 0:
        jump = prime + isqrt(prime-1) + 1
        jump = nextprime(jump - 1)
    else:
        jump = isqrt(prime-1) + 1
        jump = prevprime(jump + 1)
    return jump
def a(n):
    p = nextprime(1,n)
    count = 0
    while p != 2:
        p = hailstone(p)
        count += 1
    return count

