from math import prod
from sympy import isprime
def ok(n):
    if not isprime(n): return False
    s = str(n)
    p, np = [d for d in s if d in "2357"], [d for d in s if d in "014689"]
    return p and prod(map(int, p)) == prod(map(int, np))
