# The Akiyama-Tanigawa algorithm for powers generates the rows.
def ATPowList(n, len):
    A = [0] * len
    R = [0] * len
    for k in range(len):
        R[k] = k**n   # Changing this to R[k] = (n + 1)**k generates A344499.
        for j in range(k, 0, -1):
            R[j - 1] = j * (R[j] - R[j - 1])
        A[k] = R[0]
    return A
for n in range(8): print([n], ATPowList(n, 9))

