# OEIS sequence: A372112

from math import isqrt
from sympy import mobius, isprime
def A372112(n):
    def f(x): return int(n-sum(mobius(k)*(x//k**2) for k in range(2, isqrt(x)+1)))
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
    return m if isprime(m) else 1 # _Chai Wah Wu_, Dec 23 2024

