from itertools import combinations, count, islice
def c(n, s, a): # test the condition for subset s
    for ii, i in enumerate(s):
        for j in s[ii:]:
            if i+j <= n:
                if a[i] + a[j] != a[i+j]:
                    return False
            else:
                break
    return True
def agen(): # generator of terms
    a, valid = [None, 1], [tuple()]
    yield 1
    for n in count(1):
        new_valid, r = [], 0
        for s in valid:
            if c(n, s, a):
                new_valid.extend([s, s+(n,)])
                r = max(r, len(s)+1)
        valid = new_valid
        yield r
        a.append(r)
