from math import sqrt
def a_list(b: int, a: list[int] = [0], i: int = 1) -> list[int]:
    if sqrt(abs(a[-1] - i)).is_integer() and not (i in a):
        a += [i]
        if b < len(a):
            return a
        else:
            return a_list(b, a)
    else:
        return a_list(b, a, int(i < 0) - i)
