def a(n):
    def recurse(k, b, w):
        if k > n: return 0
        s = recurse(k+1, b, w)
        b += (1<<k)
        if not w & (b<<k): s = max(s, k+recurse(k+1, b, w+(b<<k)))
        return s
    return recurse(1, 0, 0)
