# OEIS sequence: A377897

from sympy import primepi, prevprime
def A377897(n):
    def f(x):
        if x<=3: return n+x
        p = prevprime(x+1)
        i = int(primepi(p))
        return n+x-(p>>1)-(x-p-((i^x)&1)>>1)
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
    return m # _Chai Wah Wu_, Nov 13 2024

