from sympy import factorint
def a(n):
    reach = set()
    while n not in reach:
        reach.add(n)
        n = 2 + sum(factorint(n))
    return len(reach)
print([a(n) for n in range(1, 101)]) # _Michael S. Branicky_, Nov 26 2024

