from gmpy2 import digits
def a(n):
    d, nz = list(digits(n, 3)), 0
    for i, di in enumerate(d):
        if di != '0':
            nz += 1
            if nz&1: d[i] = '2' if di == '1' else '1'
    return int("".join(d), 3)
print([a(n) for n in range(68)]) # _Michael S. Branicky_, Jan 24 2025

