from sympy import prime
from math import isqrt
def a(n):
  p = prime(n)
  if p & 3 == 1: return (p*(p-1)*(2*p+1))//2
  m = isqrt(p-1)
  return (p-1) * p**2 + (m*(m+1)*(2*m+1))//6 + sum(pow(k,2,p) for k in range(m+1,p))
print([a(n) for n in range(2, 37) ])

