from sympy import isprime
from itertools import count, islice
def agen(): # generator of terms
    yield from [2, 3]
    primesum, an = 5, 3
    while True:
        an += primesum
        if isprime(an): primesum += an
        yield an
print(list(islice(agen(), 40))) # _Michael S. Branicky_, Feb 19 2025

