from math import isqrt
from itertools import count, islice
def cond(n): return isqrt(m:=8*n+1)**2 == m
def agen(): # generator of terms
    an, aset, m = 0, {0}, 1
    for n in count(0):
        yield an
        an = next(s for k in count(m) for s in [k, -k] if s not in aset and cond(abs(an-s)))
