# OEIS sequence: A383666

def A383666(n):
    def f(x):
        if x<=1: return n+x
        l, s = x.bit_length(), bin(x)[2:]
        if (m:=s.count('0'))>0: return n+s.index('0')-(m>1)+(l*(l-1)>>1)
        return n-1+(l*(l+1)>>1)
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
    return m # _Chai Wah Wu_, May 21 2025

