# predicate test useful for large n (cf. a-file of largest terms)
from gmpy2 import digits, is_prime, mpz
def ok(n):
    s = digits(n)
    return is_prime(n) and all(int(is_prime(mpz(s[:-i]))) == 1-i&1 for i in range(1, len(s)))
