def A384021(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        kmin = kmax >> 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x):
        if x<=1: return n
        l, s = x.bit_length(), bin(x)[2:]
        if (m:=s.count('0'))>0: return n+x-s.index('0')+(m>1)-(l*(l-1)>>1)
        return n+x+1-(l*(l+1)>>1)
    return bisection(f,n,n) # _Chai Wah Wu_, May 21 2025

