# OEIS sequence: A384518

from math import isqrt
from sympy import mobius, integer_nthroot
def A384518(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        kmin = kmax >> 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def g(x): return sum(mobius(k)*(x//k**2) for k in range(1, isqrt(x)+1))
    def f(x): return n+x-sum(g(integer_nthroot(x,e)[0])-1 for e in range(3,x.bit_length(),2))
    return bisection(f,n,n) # _Chai Wah Wu_, Jun 01 2025

