from math import comb
def a(n: int) -> int:
    if n < 2: return n + 1
    s = sum(comb(n, k) % 4 for k in range((n + 1) // 2))
    return (s + (0 == n & (n - 1))) * 2
print([a(n) for n in range(68)])  # _Peter Luschny_, Jun 25 2025

