from sympy import isprime
def is_TP_pi_2(j, k):
    N = 2**j * 3**k
    return isprime(N-1) and isprime(N+1)
def aupto(limit):
    result = [1, 1]
    for exponent_sum in range(3, limit+1, 2):
        for j in range(1, exponent_sum):
             k = exponent_sum - j
             if is_TP_pi_2(j, k):
                  result.append(j)
                  result.append(k)
    return result
