from sympy import prime
def a(n):
   p = prime(n)
   s = invfact = 1
   for i in range(1, p):
       invfact = (invfact * pow(i, -1, p)) % p
       s += invfact
   return s % p # _David Radcliffe_, Jun 25 2025

