def prime_factors(n):
  i = 2
  factors = [1]
  while i * i <= n:
    if n % i:
      i += 1
    else:
      n //= i
      factors.append(i)
  if n > 1:
    factors.append(n)
  return factors
def sum_of_factors_chain(N):
  ctr, cur = 0, N
  seen = set()
  while cur not in seen:
    factors = prime_factors(cur)
    seen.add(cur)
    cur = sum(factors)
    ctr += 1
  return ctr

