import math
def a(n):
    if n == 0: return 4
    min_val = 3*(n+2)
    for x in range(1, math.isqrt(n)+1):
        y = (n + x - 1) // x
        if (x + 2) * (y + 2) < min_val:
                min_val = (x + 2) * (y + 2)
    return min_val

