from math import prod
from sympy import primerange
from itertools import combinations
def a(n):
    m, plst = float('inf'), list(primerange(1, n+1))
    for r in range(1, len(plst)+1):
        if sum(plst[:r]) > n: break
        tlb = plst[0]**(n-r)*prod(plst[:r])
        if tlb > m: continue
        for c in combinations(plst, r):
            if c[0]**n > m: break
            if sum(c) == n:
                t = c[0]**(n-len(c)+1)*prod(c[1:])
                m = min(m, t)
    return m if m < float('inf') else -1
print([a(n) for n in range(2, 33)]) # _Michael S. Branicky_, Oct 29 2025

