from gmpy2 import digits
from itertools import count
def a(n):
    if str(n).strip('0') == '1': return 2*int(n == 1)
    return next(b for b in count(2) if n==abs(sum(-d if i&1 else d for i, d in enumerate(map(int, digits(n**b))))))
