from math import factorial
def lista(num_terms):
    a = [factorial(n) - 1 for n in range(num_terms + 1)]
    b = [factorial(n) for n in range(num_terms + 1)]
    c = [0] * (num_terms + 1)
    c[0] = a[0] // b[0]
    for n in range(1, num_terms + 1):
        s = sum(c[k] * b[n - k] for k in range(0, n))
        c[n] = a[n] - s
    return c
