from math import isqrt
def odd_pp_diffs(n):
    p = {1}
    for a in range(2, isqrt(n)+1):
        x = a * a
        while x <= n:
            p.add(x)
            x *= a
    p = sorted(p)
    return [p[i+1] - p[i] for i in range(len(p)-1) if p[i] & 1 and p[i+1] & 1]
print(odd_pp_diffs(2 * 10**6))

