from math import isqrt
def even_pp_diffs(n):
    p = {1}
    for a in range(2, isqrt(n)+1):
        x = a * a
        while x <= n:
            p.add(x)
            x *= a
    p = sorted(p)
    return [p[i+1] - p[i] for i in range(len(p)-1) if p[i] % 2 == 0 and p[i+1] % 2 == 0]
print(even_pp_diffs(2 * 10**6))

