from math import isqrt
from sympy import isprime
from sympy.ntheory import digits
def ok(n): return n < 3 or (n > 3 and isprime(n-1) and all(sum(digits(n, b)[1:])%b for b in range(2, isqrt(n)+1)))
print([k for k in range(1, 6000) if ok(k)]) # _Michael S. Branicky_, Sep 19 2025

