from math import comb
from sympy import primepi, integer_nthroot, factorint
def A389479(n):
    def f(x): return int(n+1+sum(primepi(integer_nthroot(x,k)[0]) for k in range(1,x.bit_length())))
    m, k = n, f(n)
    while m != k:
        m, k = k, f(k)
    return -m+sum(sum(comb(m,p**d) for d in range(1,e+1))*(p-1) for p,e in factorint(m).items()) # _Chai Wah Wu_, Oct 11 2025

