import math
def is_square(n: int) -> bool:
    if n < 0: return False
    r = math.isqrt(n)
    return r * r == n
def a(n: int) -> int:  # by solving a Pell equation
    if is_square(n): return 0
    x = 3
    while True:
        if is_square(n * x * x + (1 - n)):
            d, m = divmod(x * x - 1, 8)
            if m == 0: return d
        x += 2
print([a(n) for n in range(1, 61)])  # _Peter Luschny_, Oct 10 2025

