from sympy import factorint
from itertools import permutations
def a(n):
    if n == 1: return 1
    m, argm = 0, None
    for p in permutations(sorted(str(n))):
        t = int("".join(p))
        f = 0 if t == 1 else max(factorint(t))
        if f > m: m, argm = f, t
    return argm
